import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Upload from "./artifacts/contracts/Upload.sol/Upload.json";
import { useState, useEffect } from "react";
import { ethers } from "ethers";
import FileUpload from "./components/FileUpload";
import Display from "./components/Display";
import Modal from "./components/Modal";
import Header from "./components/header";
import Feedback from "./components/Feedback";
import axios from "axios";
import "./App.css";

function App() {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [provider, setProvider] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [uploadedRecords, setUploadedRecords] = useState([]);
  const [userName, setUserName] = useState("");
  const [inputA, setInputA] = useState("");
  const [inputB, setInputB] = useState("");
  const [resultFromPython, setResultFromPython] = useState("");

  useEffect(() => {
    const loadProvider = async () => {
      try {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        if (provider) {
          window.ethereum.on("chainChanged", () => {
            window.location.reload();
          });

          window.ethereum.on("accountsChanged", () => {
            window.location.reload();
          });

          await provider.send("eth_requestAccounts", []);
          const signer = provider.getSigner();
          const address = await signer.getAddress();
          setAccount(address);
          const contractAddress = "0xDc64a140Aa3E981100a9becA4E685f962f0cF6C9";
          const contract = new ethers.Contract(
            contractAddress,
            Upload.abi,
            signer
          );
          setContract(contract);
          setProvider(provider);
          fetchUploadedRecords(contract, address);
          const name = await contract.getName(address);
          if (name) {
            setUserName(name);
          }
        } else {
          console.error("Metamask is not installed");
        }
      } catch (error) {
        console.error("Error loading provider:", error);
      }
    };

    loadProvider();
  }, []);

  const fetchUploadedRecords = async (contract, address) => {
    try {
      const records = await contract.display(address);
      setUploadedRecords(records);
    } catch (error) {
      console.error("Error fetching uploaded records:", error);
    }
  };

  const sendParametersToPython = async (a, b) => {
    try {
      const response = await axios.post('http://localhost:5000/', {
        a: a,
        b: b,
      });

      if (response.status === 200) {
        const data = response.data;
        setResultFromPython(data.result);
        return data.result;
      } else {
        console.error('Network response was not ok');
        return null;
      }
    } catch (error) {
      console.error('Error:', error);
      return null;
    }
  };

  const setPythonResultOnContract = async (resultInEth, maxFeePerGas) => {
    try {
      const signer = provider.getSigner();
      const contractWithSigner = contract.connect(signer);

      // Convert result to wei
      const resultInWei = ethers.utils.parseUnits(resultInEth.toString(), 'ether');

      // Send the transaction with automatic gas estimation and specified maxFeePerGas
      const tx = await contractWithSigner.setPythonResult(resultInWei, {
        gasPrice: maxFeePerGas,
      });

      await tx.wait();
      console.log("Python result stored on the smart contract");
    } catch (error) {
      console.error("Error setting Python result:", error);
    }
  };


  const handleSubmit = async () => {
    const a = parseFloat(inputA);
    const b = parseFloat(inputB);

    if (!isNaN(a) && !isNaN(b)) {
      try {

        const pythonResult = await sendParametersToPython(a, b);

        if (pythonResult !== null) {

          setResultFromPython(pythonResult);


          await setPythonResultOnContract(pythonResult);
        }
      } catch (error) {
        console.error("Error sending parameters to Python:", error);
      }
    } else {
      console.error("Invalid input for 'a' and/or 'b'");
    }
  };

  return (
    <Router>
      {!modalOpen && (
        <button className="share" onClick={() => setModalOpen(true)}>
          Share
        </button>
      )}
      {modalOpen && <Modal setModalOpen={setModalOpen} contract={contract} />}

      <div className="App">
        <Header />

        <input
          type="text"
          placeholder="Your Name"
          value={userName}
          onChange={(e) => setUserName(e.target.value)}
        />
        <button onClick={() => contract.setName(userName)}>Set Name</button>
        <div className="resultFromPython">
          Result from Python: {resultFromPython}
        </div>
        <div className="inputFields">
          <input
            type="text"
            placeholder="Enter 'a'"
            value={inputA}
            onChange={(e) => setInputA(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter 'b'"
            value={inputB}
            onChange={(e) => setInputB(e.target.value)}
          />
          <button onClick={handleSubmit}>Submit</button>
        </div>

        <Routes>
          <Route
            path="/"
            element={
              <FileUpload account={account} provider={provider} contract={contract} />
            }
          />
          <Route
            path="/display"
            element={<Display contract={contract} account={account} />}
          />
          <Route
            path="/feedback"
            element={
              <Feedback
                contract={contract}
                account={account}
                uploadedRecords={uploadedRecords}
                userName={userName}
              />
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
