import React, { useState } from "react";
import axios from "axios";
import "./FileUpload.css";

const FileUpload = ({ contract, account, provider, goBack }) => {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState("No patient record Selected");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (file) {
      try {
        const formData = new FormData();
        formData.append("file", file);

        const resFile = await axios({
          method: "post",
          url: "https://api.pinata.cloud/pinning/pinFileToIPFS",
          data: formData,
          headers: {
            pinata_api_key: `
            6d48b94084c9dfa97228`,
            pinata_secret_api_key: `9eccaeaa677cccfe553a622fa26b58eb711e9a01241af1c98a2f683df3e8df10f`,
            "Content-Type": "multipart/form-data",
          },
        });

        const ImgHash = `https://gateway.pinata.cloud/ipfs/${resFile.data.IpfsHash}`;
        contract.add(ImgHash);

        alert("Successfully Image Uploaded");
        setFileName("No Patient Record  Selected");
        setFile(null);
      } catch (e) {
        alert("Unable to upload image to Pinata");
      }
    }

    alert("Successfully Image Uploaded");
    setFileName(" No Patient Record Selected");
    setFile(null);
  };

  const retrieveFile = (e) => {
    const data = e.target.files[0];
    const reader = new window.FileReader();
    reader.readAsArrayBuffer(data);
    reader.onloadend = () => {
      setFile(e.target.files[0]);
    };
    setFileName(e.target.files[0].name);
    e.preventDefault();
  }; return (
    <div className="file-upload-container">
      <form className="form" onSubmit={handleSubmit}>
      
        <input
          disabled={!account}
          type="file"
          id="file-upload"
          name="data"
          onChange={retrieveFile} St
        />
        <br></br>
        <span className="selected-file" style={{ color: "white" }}>
  Selected File: {fileName}
</span>

        <div className="buttons-container">
          <button type="submit" className="upload-button" disabled={!file}>
            Upload Patient Record 
          </button>
          
        </div>
      </form>
    </div>
  );
};

export default FileUpload;
